import React, { useContext, useState } from 'react';
import { Card, Button, ButtonGroup } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { CartContext } from '../CartContext';

const Item = ({ item }) => {
  const { addToCart } = useContext(CartContext);
  const [quantity, setQuantity] = useState(1);
  const [showCounter, setShowCounter] = useState(false);

  const handleDecrease = () => {
    if (quantity > 1) {
      setQuantity(quantity - 1);
    }
  };

  const handleIncrease = () => {
    setQuantity(quantity + 1);
  };

  const handleAddToCart = () => {
    addToCart({ ...item, quantity });
    setShowCounter(true);
  };

  return (
    <Card style={{ width: '18rem' }}>
      <Card.Img variant="top" src={item.image} />
      <Card.Body>
        <Card.Title>{item.title}</Card.Title>
        <Card.Text>
          {item.description}
        </Card.Text>
        <ButtonGroup aria-label="Basic example">
          <Button variant="primary" as={Link} to={`/products/${item.id}`}>
            Ver detalles
          </Button>
          {showCounter && (
            <>
              <ButtonGroup>
                <Button variant="secondary" onClick={handleDecrease}>-</Button>
                <Button variant="secondary" disabled>{quantity}</Button>
                <Button variant="secondary" onClick={handleIncrease}>+</Button>
              </ButtonGroup>
              <Button variant="success" onClick={handleAddToCart}>
                Agregar al carrito
              </Button>
            </>
          )}
          {!showCounter && (
            <Button variant="success" onClick={handleAddToCart}>
              Agregar al carrito
            </Button>
          )}
        </ButtonGroup>
      </Card.Body>
    </Card>
  );
};

export default Item;
