import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Item from './Item';

const ItemListContainer = () => {
  let { id } = useParams();
  const [items, setItems] = useState([]);

  useEffect(() => {
    // Aquí normalmente harías una llamada a una API o algo similar para obtener tus items
    // Pero por ahora, vamos a simularlo con un array de objetos
    const mockItems = [
      { id: '1', title: 'Artículo 1', image: '' },
      { id: '2', title: 'Artículo 2', image: 'url_de_imagen' },
      // Agrega aquí los otros 6 items...
    ];

    setItems(mockItems);
  }, [id]);

  return (
    <div>
      {items.map(item => <Item key={item.id} item={item} />)}
    </div>
  );
}

export default ItemListContainer;
