import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import NavBar from './components/NavBar';
import ItemListContainer from './components/ItemListContainer';
import ItemDetailContainer from './components/ItemDetailContainer';
import 'bootstrap/dist/css/bootstrap.min.css';

const App = () => {
  const [cart, setCart] = useState({});

  const addToCart = (id, quantity) => {
    setCart(prev => ({
      ...prev,
      [id]: (prev[id] || 0) + quantity,
    }));
  }

  return (
    <Router>
      <div>
        <NavBar cart={cart} /> 
        <Routes>
          <Route path="/" element={<ItemListContainer greeting="Bienvenido a mi tienda!" addToCart={addToCart} />} /> 
          <Route path="/category/:id" element={<ItemListContainer greeting="Bienvenido a la categorÃ­a!" addToCart={addToCart} />} /> 
          <Route path="/item/:id" element={<ItemDetailContainer />} /> 
        </Routes>
      </div>
    </Router>
  );
};

export default App;
